import { SandpackProvider, SandpackPreview as SandpackPreviewComponent, SandpackLayout } from "@codesandbox/sandpack-react";
import { useEffect } from "react";

interface SandpackPreviewProps {
  filePath: string;
  content: string;
  className?: string;
}

function SandpackPreviewInner() {
  useEffect(() => {
    // Suppress telemetry errors by catching failed fetch to col.csbops.io
    const originalFetch = window.fetch;
    window.fetch = function(...args) {
      const url = args[0]?.toString() || '';
      if (url.includes('col.csbops.io') || url.includes('csbops')) {
        // Silently ignore telemetry requests
        return Promise.resolve(new Response('{}', { status: 200 }));
      }
      return originalFetch.apply(this, args);
    };

    return () => {
      window.fetch = originalFetch;
    };
  }, []);

  return (
    <SandpackLayout style={{ height: '100%', width: '100%' }}>
      <SandpackPreviewComponent
        showOpenInCodeSandbox={false}
        showRefreshButton={false}
        showErrorScreen={false}
        style={{ height: '100%', width: '100%' }}
      />
    </SandpackLayout>
  );
}

export function SandpackPreview({ filePath, content, className = "" }: SandpackPreviewProps) {
  return (
    <div className={`h-full w-full bg-white ${className}`} style={{ position: 'relative', height: '100%', width: '100%' }}>
      <div style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0 }}>
        <SandpackProvider
          template="react-ts"
          files={{
            "/App.tsx": content
          }}
          customSetup={{
            dependencies: {
              "spectacle": "^10.0.0",
              "react": "^18.2.0",
              "react-dom": "^18.2.0"
            }
          }}
          options={{
            autorun: true,
            autoReload: true
          }}
          theme="light"
          style={{ height: '100%', width: '100%' }}
        >
          <SandpackPreviewInner />
        </SandpackProvider>
      </div>
    </div>
  );
}

